package ovh

import (
	"fmt"
	"os"
	"testing"

	"github.com/hashicorp/terraform-plugin-sdk/v2/helper/acctest"
	"github.com/hashicorp/terraform-plugin-sdk/v2/helper/resource"
)

func TestAccCloudProjectKubeOIDCDataSource_basic(t *testing.T) {
	name := acctest.RandomWithPrefix(test_prefix)
	region := os.Getenv("OVH_CLOUD_PROJECT_KUBE_REGION_TEST")

	config := fmt.Sprintf(
		testAccCloudProjectKubeOIDCDataSourceConfig,
		os.Getenv("OVH_CLOUD_PROJECT_SERVICE_TEST"),
		name,
		region,
	)

	resource.Test(t, resource.TestCase{
		PreCheck: func() {
			testAccPreCheckKubernetes(t)
		},
		Providers: testAccProviders,
		Steps: []resource.TestStep{
			{
				Config: config,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "client_id", "toto"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "issuer_url", "https://www.ovhcloud.com/fr/"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_username_claim", "usrClaim"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_username_prefix", "usrPrefix"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_groups_claim.0", "grpClaim"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_groups_prefix", "grpPrefix"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_required_claim.0", "claim1=val1"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_required_claim.1", "claim2=val2"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_signing_algs.0", "RS512"),
					resource.TestCheckResourceAttr(
						"data.ovh_cloud_project_kube_oidc.oidcData", "oidc_ca_content", ""),
				),
			},
		},
	})
}

var testAccCloudProjectKubeOIDCDataSourceConfig = `
resource "ovh_cloud_project_kube" "cluster" {
	service_name  = "%s"
	name          = "%s"
	region        = "%s"
}

resource "ovh_cloud_project_kube_oidc" "oidc" {
	service_name  = ovh_cloud_project_kube.cluster.service_name
	kube_id       = ovh_cloud_project_kube.cluster.id
	
	client_id = "toto"
	issuer_url = "https://www.ovhcloud.com/fr/"
	oidc_username_claim = "usrClaim"
	oidc_username_prefix = "usrPrefix"
	oidc_groups_claim = ["grpClaim"]
	oidc_groups_prefix = "grpPrefix"
	oidc_required_claim = ["claim1=val1","claim2=val2"]
	oidc_signing_algs = ["RS512"]
	oidc_ca_content = ""

	depends_on = [
		ovh_cloud_project_kube.cluster
	]

}

data "ovh_cloud_project_kube_oidc" "oidcData" {
    service_name = ovh_cloud_project_kube.cluster.service_name
    kube_id = ovh_cloud_project_kube.cluster.id

	depends_on = [
		ovh_cloud_project_kube_oidc.oidc
	]
}
`
