package ovh

import (
	"fmt"
	"os"
	"testing"

	"github.com/hashicorp/terraform-plugin-testing/helper/acctest"
	"github.com/hashicorp/terraform-plugin-testing/helper/resource"
	"github.com/hashicorp/terraform-plugin-testing/terraform"
)

var testAccCloudProjectKubeOIDCConfig = `
	resource "ovh_cloud_project_kube" "cluster" {
		service_name  = "%s"
		name          = "%s"
		region        = "%s"
	}
	resource "ovh_cloud_project_kube_oidc" "my-oidc" {
		service_name  = ovh_cloud_project_kube.cluster.service_name
		kube_id       = ovh_cloud_project_kube.cluster.id
  		client_id    = "%s"
  		issuer_url   = "%s"

		oidc_username_claim = "usrClaim"
		oidc_username_prefix = "usrPrefix"
		oidc_groups_claim = ["grpClaim"]
		oidc_groups_prefix = "grpPrefix"
		oidc_required_claim = ["claim1=val1","claim2=val2"]
		oidc_signing_algs = ["RS512"]
		oidc_ca_content = ""

	}
`

func TestAccCloudProjectKubeOIDC_full(t *testing.T) {
	region := os.Getenv("OVH_CLOUD_PROJECT_KUBE_REGION_TEST")
	name := acctest.RandomWithPrefix(test_prefix)
	resourceName := "ovh_cloud_project_kube_oidc.my-oidc"

	config := fmt.Sprintf(
		testAccCloudProjectKubeOIDCConfig,
		os.Getenv("OVH_CLOUD_PROJECT_SERVICE_TEST"),
		name,
		region,
		"my-oidc-client-id",
		"https://ovh.com",
	)

	configUpdated := fmt.Sprintf(
		testAccCloudProjectKubeOIDCConfig,
		os.Getenv("OVH_CLOUD_PROJECT_SERVICE_TEST"),
		name,
		region,
		"my-another-oidc-client-id",
		"https://docs.ovh.com",
	)

	resource.Test(t, resource.TestCase{
		PreCheck: func() {
			testAccPreCheckCloud(t)
			testAccCheckCloudProjectExists(t)
			testAccPreCheckKubernetes(t)
		},
		Providers: testAccProviders,
		Steps: []resource.TestStep{
			{
				Config: config,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "client_id", "my-oidc-client-id"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "issuer_url", "https://ovh.com"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_username_claim", "usrClaim"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_username_prefix", "usrPrefix"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_groups_claim.0", "grpClaim"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_groups_prefix", "grpPrefix"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_required_claim.0", "claim1=val1"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_required_claim.1", "claim2=val2"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_signing_algs.0", "RS512"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_ca_content", ""),
				),
			},
			{
				ResourceName:      resourceName,
				ImportState:       true,
				ImportStateVerify: true,
				ImportStateIdFunc: func(state *terraform.State) (string, error) {
					return fmt.Sprintf("%s/%s", os.Getenv("OVH_CLOUD_PROJECT_SERVICE_TEST"), state.RootModule().Resources[resourceName].Primary.Attributes["kube_id"]), nil
				},
			},
			{
				Config: configUpdated,
				Check: resource.ComposeTestCheckFunc(
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "client_id", "my-another-oidc-client-id"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "issuer_url", "https://docs.ovh.com"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_username_claim", "usrClaim"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_username_prefix", "usrPrefix"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_groups_claim.0", "grpClaim"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_groups_prefix", "grpPrefix"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_required_claim.0", "claim1=val1"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_required_claim.1", "claim2=val2"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_signing_algs.0", "RS512"),
					resource.TestCheckResourceAttr("ovh_cloud_project_kube_oidc.my-oidc", "oidc_ca_content", ""),
				),
			},
			{
				Config:       configUpdated,
				Destroy:      true,
				ResourceName: resourceName,
			},
		},
	})
}
